CREATE OR REPLACE FUNCTION obter_qt_conv_oci_inspecao ( nr_sequencia_p bigint, nr_ordem_compra_p bigint, nr_item_oci_p bigint, nr_seq_entrega_p bigint) RETURNS bigint AS $body$
DECLARE


qt_prevista_entrega_w		double precision;


BEGIN

if (nr_seq_entrega_p > 0) then
	select (CASE WHEN obter_dados_material(a.cd_material,'UME')=a.cd_unidade_medida_compra THEN 			1  ELSE obter_conversao_material(a.cd_material, 'CE') END  * coalesce(b.qt_prevista_entrega,0))
	into STRICT	qt_prevista_entrega_w
	from	ordem_compra_item a,
		ordem_compra_item_entrega b
	where	b.nr_sequencia = nr_seq_entrega_p
	and	b.nr_item_oci = a.nr_item_oci
	and	b.nr_ordem_compra = a.nr_ordem_compra;
else
	select (CASE WHEN obter_dados_material(a.cd_material,'UME')=a.cd_unidade_medida_compra THEN 			1  ELSE obter_conversao_material(a.cd_material, 'CE') END  * coalesce(a.qt_material,0))
	into STRICT	qt_prevista_entrega_w
	from	ordem_compra_item a
	where	a.nr_ordem_compra = nr_ordem_compra_p
	and	a.nr_item_oci = nr_item_oci_p;
end if;

return	qt_prevista_entrega_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
