CREATE OR REPLACE FUNCTION obter_qt_desc_mat_compras ( cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_cont_w	bigint;


BEGIN
select	count(*)
into STRICT	qt_cont_w
from	regra_desc_material_compra
where	coalesce(cd_grupo_material, obter_estrutura_material(cd_material_p,'G'))	= obter_estrutura_material(cd_material_p,'G')
and	coalesce(cd_subgrupo_material, obter_estrutura_material(cd_material_p,'S'))	= obter_estrutura_material(cd_material_p,'S')
and	coalesce(cd_classe_material, obter_estrutura_material(cd_material_p,'C'))	= obter_estrutura_material(cd_material_p,'C')
and	coalesce(cd_material, cd_material_p) = cd_material_p
and	cd_estabelecimento = cd_estabelecimento_p;

return	qt_cont_w;

end;
$body$
