CREATE OR REPLACE FUNCTION obter_qt_dias_aberto (dt_inicio_p timestamp, cd_estabelecimento_p text, ie_status_p text) RETURNS bigint AS $body$
DECLARE


qt_dias_w		varchar(60) := 0;
qt_var_w		integer;
qt_dias_func_w		integer    := 0;
horas_atend_w		varchar(10);
horas_atual_w		varchar(10);


BEGIN

if((ie_status_p = 'A') or (ie_status_p = 'G') or (ie_status_p = 'P')) then


	while(qt_dias_func_w + trunc(dt_inicio_p) <= trunc(clock_timestamp())) loop

		select	CASE WHEN (OBTER_SE_DIA_UTIL((trunc(dt_inicio_p) + qt_dias_func_w), cd_estabelecimento_p))='S' THEN  1  ELSE 0 END
		into STRICT 	qt_var_w
		;

		qt_dias_w := qt_dias_w + qt_var_w;
		qt_dias_func_w := qt_dias_func_w + 1;
	end loop;

	select	to_char(dt_inicio_p, 'hh24:mi:ss'),
		to_char(clock_timestamp(), 'hh24:mi:ss')
	into STRICT	horas_atend_w,
		horas_atual_w
	;

	if (horas_atend_w < horas_atual_w) then
		qt_dias_w := qt_dias_w - 1;
	else
		qt_dias_w := qt_dias_w - 2;
	end if;

	return	qt_dias_w;

end if;

return	null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
