CREATE OR REPLACE FUNCTION obter_qt_dias_autorizados (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dia_autorizados_w	bigint;


BEGIN

select	sum(qt_dia_autorizado)
into STRICT	qt_dia_autorizados_w
from	autorizacao_convenio
where	nr_atendimento	= nr_atendimento_p
and	ie_tipo_autorizacao	in ('1','2');

return	qt_dia_autorizados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
