CREATE OR REPLACE FUNCTION obter_qt_dias_pagamento (cd_forma_pagamento_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint;


BEGIN

select 	max(coalesce(qt_dias_parcela,0))
into STRICT 	ds_retorno_w
from	parcela
where 	cd_condicao_pagamento	= cd_forma_pagamento_p;


return	ds_retorno_w;

end;
