CREATE OR REPLACE FUNCTION obter_qt_dias_tipo_carencia ( nr_seq_tipo_carencia_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint;


BEGIN

if (coalesce(nr_seq_tipo_carencia_p,0) > 0) then

	select	max(qt_dias_padrao)
	into STRICT	ds_retorno_w
	from	pls_tipo_carencia
