CREATE OR REPLACE FUNCTION obter_qt_dias_venc (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
dt_prorrogacao_w	timestamp;
dt_liquidacao_w	timestamp;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	begin

	select	max(coalesce(a.dt_pagamento_previsto, a.dt_vencimento))
	into STRICT	dt_prorrogacao_w
	from	titulo_receber a
	where	a.nr_titulo = nr_titulo_p;

	select	max(a.dt_liquidacao)
	into STRICT	dt_liquidacao_w
	from	titulo_receber a
	where	a.nr_titulo = nr_titulo_p;

	if ((dt_prorrogacao_w IS NOT NULL AND dt_prorrogacao_w::text <> '') and coalesce(dt_liquidacao_w::text, '') = '') then

		ds_retorno_w:= substr(to_char(obter_dias_entre_datas(dt_prorrogacao_w, clock_timestamp())),1,10);

		if (ds_retorno_w < 0) then

			ds_retorno_w := 0;

		end if;

	end if;

	if (dt_liquidacao_w IS NOT NULL AND dt_liquidacao_w::text <> '') then

			ds_retorno_w := 0;

	end if;


	exception when others then
		ds_retorno_w := null;
	end;

end if;

return	substr(coalesce(ds_retorno_w,0),1,254);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
