SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_dia_internado ( nr_atendimento_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


qt_dia_internado_w	bigint := 0;
dt_entrada_w		timestamp;
ie_dias_alta_w		varchar(1);
nr_atend_alta_w		bigint;
nr_atendimento_w	bigint;
qt_dias_alta_w		smallint;

/*
P - primeiro atendimento do paciente com altas administrativas;
U - Ãºltimo atendimento do paciente.
*/
BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin
	ie_dias_alta_w := Obter_Param_Usuario(44, 155, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_dias_alta_w);

	if (ie_dias_alta_w = 'N') then
		begin
		nr_atend_alta_w	:= 	nr_atendimento_p;
		nr_atendimento_w := 	nr_atendimento_p;
		select	count(*)
		into STRICT	qt_dias_alta_w
		from 	atendimento_paciente a
		where	a.nr_atendimento =	nr_atendimento_p
		and	(a.nr_atend_alta IS NOT NULL AND a.nr_atend_alta::text <> '');

		if (qt_dias_alta_w = 0) then
			ie_dias_alta_w := 'S';
			nr_atend_alta_w	:= 0;
		end if;

		while(nr_atend_alta_w <> 0)
			loop
			select	max(a.nr_atend_alta),
				max(a.dt_entrada)
			into STRICT	nr_atend_alta_w,
				dt_entrada_w
			from 	atendimento_paciente a
			where	a.nr_atendimento = 	(SELECT b.nr_atend_alta
							from atendimento_paciente b
							where b.nr_atendimento = nr_atend_alta_w);

			end loop;

		if (coalesce(dt_entrada_w::text, '') = '') then
			begin
			nr_atend_alta_w	:= 	nr_atendimento_p;
			nr_atendimento_w := 	nr_atendimento_p;
			while(nr_atend_alta_w <> 0)
				loop

				select  max(a.nr_atend_alta),
					max(a.dt_entrada)
				into STRICT    nr_atend_alta_w,
					dt_entrada_w
				from    atendimento_paciente a
				where   a.nr_atendimento =  nr_atendimento_w;

				select  max(b.nr_atend_alta)
				into STRICT    nr_atendimento_w
				from    atendimento_paciente b
				where   b.nr_atendimento = nr_atend_alta_w;
				end loop;
			end;
		end if;
		end;
	end if;
	if (ie_dias_alta_w = 'S') then
		begin
		select 	max(b.dt_entrada)
		into STRICT	dt_entrada_w
		from 	atendimento_paciente b
		where 	b.nr_atendimento = nr_atendimento_p;
		end;
	end if;

	if (dt_entrada_w IS NOT NULL AND dt_entrada_w::text <> '') then
		qt_dia_internado_w 	:= (trunc(clock_timestamp()) - trunc(dt_entrada_w));
	end if;

	end;
end if;

return	qt_dia_internado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
