CREATE OR REPLACE FUNCTION obter_qt_dia_regra_entr_laudo ( cd_procedimento_p bigint, nr_seq_proc_interno_p bigint, cd_especialidade_p bigint, ie_origem_proced_p bigint, nr_seq_agenda_exame_p bigint) RETURNS bigint AS $body$
DECLARE

			 
ds_retorno_w		bigint;
nr_sequencia_w		bigint;
qt_dias_entrega_w		bigint;
cd_area_proced_w		bigint;
cd_espec_proced_w	bigint;
cd_grupo_proced_w	bigint;
cd_setor_exclusivo_w	integer;

C01 CURSOR FOR 
 
SELECT	a.nr_sequencia	 
from	regra_entrega_laudo a 
where	((((a.dt_inicio_vigencia IS NOT NULL AND a.dt_inicio_vigencia::text <> '') and (a.dt_fim_vigencia IS NOT NULL AND a.dt_fim_vigencia::text <> '') 
and (trunc(clock_timestamp()) between trunc(a.dt_inicio_vigencia) and trunc(a.dt_fim_vigencia))) or 
	 ((a.dt_fim_vigencia IS NOT NULL AND a.dt_fim_vigencia::text <> '') and (trunc(clock_timestamp()) <= a.dt_fim_vigencia))) or (coalesce(a.dt_fim_vigencia::text, '') = '')) 
and	((a.cd_procedimento = cd_procedimento_p) or (coalesce(a.cd_procedimento::text, '') = '')) 
and	((a.nr_seq_proc_interno = nr_seq_proc_interno_p) or (coalesce(a.nr_seq_proc_interno::text, '') = '')) 
and	((a.cd_grupo_proc = cd_grupo_proced_w) or (coalesce(a.cd_grupo_proc::text, '') = '')) 
and	((a.cd_especialidade = cd_espec_proced_w) or (coalesce(a.cd_especialidade::text, '') = '')) 
and	((a.cd_area_procedimento = cd_area_proced_w) or (coalesce(a.cd_area_procedimento::text, '') = '')) 
and	((a.cd_setor_atendimento = cd_setor_exclusivo_w) or (coalesce(a.cd_setor_atendimento::text, '') = '')) 
and	ie_tipo_regra in ('A', 'AM') 
order by coalesce(a.cd_procedimento,0), 
	 coalesce(a.nr_seq_proc_interno,0), 
	 coalesce(a.cd_grupo_proc,0), 
	 coalesce(a.cd_especialidade,0), 
	 coalesce(a.cd_area_procedimento,0);
	

BEGIN 
 
cd_setor_exclusivo_w	:= Obter_Setor_exec_proc_interno(nr_seq_proc_interno_p, 
				null, 
				null, 
				null, 
				null, 
				'S');
				 
 
select	coalesce(max(cd_area_procedimento),0), 
	coalesce(max(cd_especialidade),0), 
	coalesce(max(cd_grupo_proc),0) 
into STRICT	cd_area_proced_w, 
	cd_espec_proced_w, 
	cd_grupo_proced_w 
from	estrutura_procedimento_v 
where	cd_procedimento = cd_procedimento_p 
and	ie_origem_proced = ie_origem_proced_p;		
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	nr_sequencia_w := nr_sequencia_w;
	end;
end loop;
close C01;
 
if (nr_sequencia_w > 0) then 
	select	max(qt_dia) 
	into STRICT	qt_dias_entrega_w 
	from	regra_entrega_laudo 
	where	nr_sequencia = nr_sequencia_w;
end if;
 
ds_retorno_w := qt_dias_entrega_w;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
