SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_dieta_prescr_data (nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			bigint;
nr_atendimento_w		bigint;
dt_inicio_prescr_w		timestamp;
dt_validade_prescr_w	timestamp;
ie_forma_consiste_w		varchar(5);
nm_usuario_w			varchar(50);


BEGIN

select	max(dt_inicio_prescr),
	max(dt_validade_prescr),
	max(nr_atendimento),
	max(nm_usuario)
into STRICT	dt_inicio_prescr_w,
	dt_validade_prescr_w,
	nr_atendimento_w,
	nm_usuario_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

ie_forma_consiste_w := obter_param_usuario(924, 1012, obter_perfil_ativo, nm_usuario_w, wheb_usuario_pck.get_cd_estabelecimento, ie_forma_consiste_w);

select	sum(dieta)
into STRICT	qt_retorno_w
from (
	SELECT	coalesce(count(a.nr_sequencia),0) dieta
	from	Prescr_dieta a,
			prescr_medica b
	where	a.nr_prescricao = nr_prescricao_p
	and a.nr_prescricao		= b.nr_prescricao
	and	b.nr_atendimento	= nr_atendimento_w
	and	obter_se_prescr_vig_adep(b.dt_inicio_prescr,b.dt_validade_prescr,dt_inicio_prescr_w,dt_validade_prescr_w) = 'S'
	and	coalesce(obter_datas_prescricao(b.nr_prescricao,'L')::text, '') = ''
	and	coalesce(ie_forma_consiste_w,'N') = 'N'
	and	coalesce(a.dt_suspensao::text, '') = ''	
	
union all

	SELECT	coalesce(count(a.nr_sequencia),0) dieta
	from	Prescr_dieta a,
			prescr_medica b
	where	a.nr_prescricao	= b.nr_prescricao
	and	b.nr_atendimento	= nr_atendimento_w
	and	obter_se_prescr_vig_adep(b.dt_inicio_prescr,b.dt_validade_prescr,dt_inicio_prescr_w,dt_validade_prescr_w) = 'S'
	and	(obter_datas_prescricao(b.nr_prescricao,'L') IS NOT NULL AND (obter_datas_prescricao(b.nr_prescricao,'L'))::text <> '')
	and	coalesce(ie_forma_consiste_w,'N') = 'N'
	and	coalesce(a.dt_suspensao::text, '') = ''
	and exists (	select	1
					from	prescr_dieta c
					where	c.nr_prescricao = nr_prescricao_p
					and		coalesce(c.dt_suspensao::text, '') = ''  LIMIT 1)
	
union all

	select	coalesce(count(a.nr_sequencia),0) dieta
	from	Prescr_dieta a,
			prescr_medica b
	where	a.nr_prescricao = nr_prescricao_p
	and	a.nr_prescricao		= b.nr_prescricao
	and	b.nr_atendimento	= nr_atendimento_w
	and	obter_se_prescr_vig_adep(b.dt_inicio_prescr,b.dt_validade_prescr,dt_inicio_prescr_w,dt_validade_prescr_w) = 'S'
	and	(obter_datas_prescricao(b.nr_prescricao,'L') IS NOT NULL AND (obter_datas_prescricao(b.nr_prescricao,'L'))::text <> '')
	and	coalesce(ie_forma_consiste_w,'N') = 'S'
	and	coalesce(a.dt_suspensao::text, '') = '') a;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_dieta_prescr_data (nr_prescricao_p bigint) FROM PUBLIC;

