SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_dieta_prescr ( nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	bigint := 0;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	sum(dieta)
	into STRICT	qt_retorno_w
	from (	SELECT	coalesce(count(*),0) dieta
				from	Prescr_dieta
				where	nr_prescricao = nr_prescricao_p
				
union all

				SELECT	coalesce(count(*),0) dieta
				from	REP_Jejum
				where	nr_prescricao = nr_prescricao_p) a;

end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
