CREATE OR REPLACE FUNCTION obter_qt_diferenca_md ( dt_sinal_vital_p timestamp, nr_apap_p bigint, dt_apap_p timestamp ) RETURNS bigint AS $body$
DECLARE

    nr_hora_sv_w    bigint;
    qt_diferenca_w  bigint := NULL;
    dt_apap_w       timestamp;

BEGIN
    dt_apap_w := dt_apap_p;
    nr_hora_sv_w := (to_char(round(dt_sinal_vital_p, 'hh24'), 'hh24'))::numeric;
    IF ( nr_hora_sv_w <> nr_apap_p ) THEN
        IF ( trunc(dt_apap_w, 'hh24') < trunc(dt_sinal_vital_p, 'hh24') ) THEN
            dt_apap_w := dt_apap_w + 1;
        END IF;

        qt_diferenca_w := obter_hora_entre_datas_md(dt_sinal_vital_p, dt_apap_w);

    END IF;

    RETURN qt_diferenca_w;
END;
