CREATE OR REPLACE FUNCTION obter_qt_dispensada_pepo (nr_prescricao_p bigint, cd_material_p bigint, nr_seq_lote_fornec_p bigint, ie_operacao_p text) RETURNS bigint AS $body$
DECLARE


qt_dispensado_w	double precision;


BEGIN

select	coalesce(sum(b.qt_dispensacao),0)
into STRICT	qt_dispensado_w
from	cirurgia a,
	cirurgia_agente_disp b
where	a.nr_cirurgia			= b.nr_cirurgia
and	a.nr_prescricao 		= nr_prescricao_p
and	b.ie_operacao			= ie_operacao_p
and	b.cd_material			= cd_material_p
and	coalesce(b.nr_seq_lote_fornec,0)	= coalesce(nr_seq_lote_fornec_p,0);

return	qt_dispensado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
