CREATE OR REPLACE FUNCTION obter_qt_dose_esq_alternado ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_solucao_p prescr_mat_hor.nr_seq_solucao%type) RETURNS bigint AS $body$
DECLARE


qt_dose_w			prescr_mat_hor.qt_dose%type;
nr_etapa_atual_w	prescr_mat_hor.nr_etapa_sol%type;


BEGIN

select	coalesce(min(a.nr_etapa_sol),0)
into STRICT	nr_etapa_atual_w
from	prescr_mat_hor a
where	Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S'
and		coalesce(a.dt_fim_horario::text, '') = ''
and		a.ie_agrupador = 4
and		a.nr_seq_solucao = nr_seq_solucao_p
and		a.nr_prescricao = nr_prescricao_p;

select	coalesce(sum(CASE WHEN obter_se_mat_conv_ml(a.cd_material)='S' THEN  obter_conversao_ml(a.cd_material, a.qt_dose, a.cd_unidade_medida_dose)  ELSE a.qt_dose END ),0) qt_dose
into STRICT	qt_dose_w
from	prescr_mat_hor a
where	a.nr_prescricao		= nr_prescricao_p
and		a.nr_seq_solucao	= nr_seq_solucao_p
and		a.nr_etapa_sol		= nr_etapa_atual_w;

return qt_dose_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
