CREATE OR REPLACE FUNCTION obter_qt_emprestimo ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, dt_mesano_referencia_p timestamp, ie_entrada_saida_p text) RETURNS bigint AS $body$
DECLARE


qt_saldo_w		double precision;
qt_retorno_w		double precision	:= 0;
qt_cirurgia_w		double precision	:= 0;
qt_emprestimo_w		double precision	:= 0;
cd_material_w		integer;
ie_tipo_local_w		varchar(5);
qt_conv_estoque_w	double precision;
cd_local_estoque_w	integer;
dt_mesano_referencia_w	timestamp;

c01 CURSOR FOR
	SELECT	distinct cd_local_estoque
	from	saldo_estoque
	where	cd_estabelecimento	= cd_estabelecimento_p
	  and	dt_mesano_referencia	>= dt_mesano_referencia_w
	  and	cd_local_estoque	= cd_local_estoque_p
	  and	cd_material	= cd_material_w
	  and	coalesce(cd_local_estoque_p,0) > 0
	
union

	SELECT	distinct cd_local_estoque
	from	saldo_estoque
	where	cd_estabelecimento	= cd_estabelecimento_p
	  and	dt_mesano_referencia	>= dt_mesano_referencia_w
	  and	cd_material		= cd_material_w
	  and	coalesce(cd_local_estoque_p,0) = 0;


BEGIN

select	max(dt_mesano_vigente)
into STRICT	dt_mesano_referencia_w
from	parametro_estoque
where	cd_estabelecimento	= cd_estabelecimento_p;

select	coalesce(max(qt_conv_estoque_consumo),1),
	max(cd_material_estoque)
into STRICT	qt_conv_estoque_w,
	cd_material_w
from	material
where	cd_material			= cd_material_p;

open  c01;
loop
fetch c01 into cd_local_estoque_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	qt_emprestimo_w		:= 0;
	qt_cirurgia_w		:= 0;
	qt_saldo_w := obter_saldo_estoque(cd_estabelecimento_p, cd_material_w, cd_local_estoque_w, null, qt_saldo_w);
	select	ie_tipo_local
	into STRICT	ie_tipo_local_w
	from 	local_estoque
	where	cd_local_estoque	= cd_local_estoque_w;
	if (dt_mesano_referencia_p = trunc(clock_timestamp(),'month')) or (dt_mesano_referencia_p	< clock_timestamp() - interval '2000 days') then
		select /*+ index(b empmate_i1) index (c emprest_pk) */			coalesce(sum(qt_material),0)
		into STRICT	qt_emprestimo_w
		from	emprestimo c,
			emprestimo_material b
		where	c.cd_local_estoque	= cd_local_estoque_w
		and	b.nr_emprestimo		= c.nr_emprestimo
		and	c.ie_tipo		 	= ie_entrada_saida_p
		and	c.ie_situacao		<> 'I'
		and	(c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '')
		and	b.qt_material		> 0
		and	exists (
			SELECT 1 from material a
			where	a.cd_material_estoque	= cd_material_w
		  	  and a.cd_material 		= b.cd_material);

	end if;
	qt_retorno_w	:=  qt_retorno_w + coalesce(qt_emprestimo_w,0);
	end;
end loop;
close c01;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
