CREATE OR REPLACE FUNCTION obter_qt_entregue_item_oci ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ie_canceladas_p text) RETURNS bigint AS $body$
DECLARE


qt_real_entrega_w		double precision;


BEGIN

select	coalesce(sum(qt_real_entrega),0)
into STRICT	qt_real_entrega_w
from	ordem_compra_item_entrega
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p
and	((ie_canceladas_p = 'S') or
	((ie_canceladas_p = 'N') and (coalesce(dt_cancelamento::text, '') = '')));

return	qt_real_entrega_w;

end;
$body$
