CREATE OR REPLACE FUNCTION obter_qt_entregue_oci ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS bigint AS $body$
DECLARE

			
qt_entregue_w	ordem_compra_item_entrega.qt_real_entrega%type;
			

BEGIN

select	coalesce(sum(b.qt_real_entrega),0)
into STRICT	qt_entregue_w
from	ordem_compra_item_entrega b,
	ordem_compra_item a
where	a.nr_ordem_compra = nr_ordem_compra_p
and	a.nr_item_oci = nr_item_oci_p
and	a.nr_ordem_compra = b.nr_ordem_compra
and	a.nr_item_oci = b.nr_item_oci
and	coalesce(b.dt_cancelamento::text, '') = '';

return	qt_entregue_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
