CREATE OR REPLACE FUNCTION obter_qt_equip_adquirido_data ( dt_mesano_referencia_p timestamp, nr_seq_tipo_equip_p bigint) RETURNS bigint AS $body$
DECLARE


qt_existe_w	bigint;
dt_data_w	timestamp;


BEGIN
dt_data_w	:= trunc(dt_mesano_referencia_p,'year');

select	count(*)
into STRICT	qt_existe_w
from	man_equipamento
where	trunc(dt_aquisicao,'dd') < dt_data_w
and	(dt_aquisicao IS NOT NULL AND dt_aquisicao::text <> '')
and (nr_seq_tipo_equip = nr_seq_tipo_equip_p or coalesce(nr_seq_tipo_equip_p,0) = 0);

return	qt_existe_w;

end;
