SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_erro_usuario (dt_parametro_p timestamp, nm_usuario_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
QT - Quantidade de erros no mês
PR - Percentual de erros sobre total de OS's no mês
*/
dt_ref_mes_w		timestamp;
dt_fim_mes_w		timestamp;
qt_total_os_w		bigint;
qt_erro_w			bigint;
cd_pessoa_usuario_w	varchar(10);



BEGIN
dt_ref_mes_w			:= trunc(dt_parametro_p,'month');
dt_fim_mes_w			:= last_day(dt_ref_mes_w) + 86399/86400;

select	cd_pessoa_fisica
into STRICT	cd_pessoa_usuario_w
from	usuario
where	nm_usuario = nm_usuario_p;

select	count(*)
into STRICT	qt_total_os_w
from	man_ordem_servico a
where exists (	SELECT	1
		from	man_ordem_servico_exec b
		where	b.nm_usuario_exec = nm_usuario_p
		and	b.nr_seq_ordem    = a.nr_sequencia)
and	a.dt_ordem_servico between dt_ref_mes_w and dt_fim_mes_w;

select	count(distinct a.nr_sequencia)
into STRICT	qt_erro_w
from	man_ordem_servico a,
	man_doc_erro b
where	a.nr_sequencia		= b.nr_seq_ordem
and	b.ie_origem_erro		= 'W'
and	b.cd_pessoa_fisica		= cd_pessoa_usuario_w
and	a.dt_ordem_servico between dt_ref_mes_w and dt_fim_mes_w;

if (ie_opcao_p = 'QT') then
	return qt_erro_w;
elsif (ie_opcao_p = 'PR') then
	return dividir(qt_erro_w, qt_total_os_w)*100;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
