CREATE OR REPLACE FUNCTION obter_qt_estoque_convertida ( cd_material_p bigint, qt_estoque_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_p:
'CP' - Converte para quantidade de compra
'C' - Converte para quantidade de consumo
*/
qt_convertida_w		double precision;
qt_conversao_w		double precision;


BEGIN

qt_convertida_w := coalesce(qt_estoque_p,0);

if (ie_opcao_p = 'P') then
	qt_conversao_w := obter_conversao_material(cd_material_p, 'CE');
	qt_convertida_w := dividir(coalesce(qt_estoque_p,0),qt_conversao_w);
elsif (ie_opcao_p = 'C') then
	qt_conversao_w := obter_conversao_material(cd_material_p, 'EC');
	qt_convertida_w := coalesce(qt_estoque_p,0) * qt_conversao_w;
end if;

return	qt_convertida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
