CREATE OR REPLACE FUNCTION obter_qt_etapas_validade ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_etapas_w		bigint := 0;
nr_horas_validade_w	bigint := 0;
qt_hora_fase_w		double precision := 0;


BEGIN

select	max(a.nr_horas_validade),
	max(b.qt_hora_fase)
into STRICT	nr_horas_validade_w,
	qt_hora_fase_w
from	prescr_medica a,
	prescr_solucao b
where	a.nr_prescricao = b.nr_prescricao
and	a.nr_prescricao = nr_prescricao_p
and	b.nr_seq_solucao = nr_seq_solucao_p;

while(nr_horas_validade_w	> 0) loop
	qt_etapas_w		:= qt_etapas_w + 1;
	nr_horas_validade_w	:= nr_horas_validade_w - qt_hora_fase_w;
end loop;

return	qt_etapas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
