CREATE OR REPLACE FUNCTION obter_qt_evento_upp_2 ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w 		bigint;
qt_ponto_w 		bigint;
nr_atendimento_w	bigint;


BEGIN

	SELECT	max(qt_ponto),
			max(nr_atendimento)
	INTO STRICT 	qt_ponto_w,
			nr_atendimento_w
	FROM 	atend_escala_braden b
	WHERE 	b.nr_sequencia = nr_sequencia_p;

	if (qt_ponto_w < 19) then

		qt_retorno_w := 1;

	else

		qt_retorno_w := 0;

	end if;


