SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_evento_upp_inc ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w 		bigint;
dt_evento_min_w		qua_evento_paciente.dt_evento%type;
nr_atendimento_w 	qua_evento_paciente.nr_atendimento%type;
dt_evento_w 		qua_evento_paciente.dt_evento%type;


BEGIN
	SELECT	a.nr_atendimento
			,a.dt_evento
	INTO STRICT 	nr_atendimento_w
			,dt_evento_w
	FROM 	qua_evento_paciente a
	WHERE 	a.nr_sequencia = nr_sequencia_p;

	SELECT 	min(dt_evento)
	into STRICT 	dt_evento_min_w
	FROM qua_evento_paciente a
LEFT OUTER JOIN cur_ferida b ON (a.nr_sequencia = b.nr_seq_evento)
WHERE a.nr_atendimento = nr_atendimento_w and a.nr_seq_evento = 56 and b.ie_admitido_ferida = 'N'  and coalesce(b.dt_inativacao::text, '') = '';

	SELECT	count(*) qt_evento
	INTO STRICT 	qt_retorno_w
	FROM 	qua_evento_paciente b
	WHERE 	b.nr_atendimento = nr_atendimento_w
	and 	nr_sequencia = nr_sequencia_p
	and 	dt_evento = dt_evento_w
	and		dt_evento = dt_evento_min_w;

	return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_evento_upp_inc ( nr_sequencia_p bigint) FROM PUBLIC;

