CREATE OR REPLACE FUNCTION obter_qt_evolucoes_nao_lib ( nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


qt_evo_nao_lib_w bigint := 0;


BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_evo_nao_lib_w
	from	evolucao_paciente
	where	nm_usuario = nm_usuario_p
	and	dt_evolucao > clock_timestamp() - interval '30 days'
	and	(nr_cirurgia IS NOT NULL AND nr_cirurgia::text <> '')
	and	ie_evolucao_clinica not in ('AE','CO')
	and	coalesce(dt_liberacao::text, '') = '';
	end;
end if;

return qt_evo_nao_lib_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
