CREATE OR REPLACE FUNCTION obter_qt_faltas_pac_agecons (cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, cd_agenda_p bigint, ie_forma_apres_p text) RETURNS bigint AS $body$
DECLARE

 
qt_retorno_w	bigint;
			

BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	if (ie_forma_apres_p = 'S') then 
		select	count(*) 
		into STRICT	qt_retorno_w 
		from	agenda a, 
			agenda_consulta b 
		where	a.cd_agenda	= b.cd_agenda 
		and	a.cd_estabelecimento	= cd_estabelecimento_p 
		and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p 
		and	b.ie_status_agenda	in ('F','I');
	elsif (ie_forma_apres_p = 'A') then 
		select	count(*) 
		into STRICT	qt_retorno_w 
		from	agenda a, 
			agenda_consulta b 
		where	a.cd_agenda	= b.cd_agenda 
		and	a.cd_estabelecimento	= cd_estabelecimento_p 
		and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p 
		and	a.cd_agenda		= cd_agenda_p 
		and	b.ie_status_agenda	in ('F','I');
	end if;
end if;
 
return	qt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
