CREATE OR REPLACE FUNCTION obter_qt_fator_ativ_fisica (ie_fator_ativ_fisica_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_result_w	varchar(10);
cd_pf_w bigint;
ds_sexo_w varchar(1);


BEGIN 
 
	select cd_pessoa_fisica 
	into STRICT cd_pf_w 
	from atendimento_paciente 
	where nr_atendimento = nr_atendimento_p;
 
	ds_sexo_w := obter_sexo_pf(cd_pf_w, 'C');
 
	if (ds_sexo_w = 'M')then 
		if (ie_fator_ativ_fisica_p = 'A') then 
			ds_result_w := '1,26';
		elsif (ie_fator_ativ_fisica_p = 'B') then 
			ds_result_w := '1,13';
		elsif (ie_fator_ativ_fisica_p = 'M') then 
			ds_result_w := '1,42';
		elsif (ie_fator_ativ_fisica_p = 'S') then 
			ds_result_w := '1,00';
		end if;
	else 
		if (ie_fator_ativ_fisica_p = 'A') then 
			ds_result_w := '1,31';
		elsif (ie_fator_ativ_fisica_p = 'B') then 
			ds_result_w := '1,16';
		elsif (ie_fator_ativ_fisica_p = 'M') then 
			ds_result_w := '1,53';
		elsif (ie_fator_ativ_fisica_p = 'S') then 
			ds_result_w := '1,00';
		end if;
	end if;
 
return ds_result_w;
 
end;
$body$
