CREATE OR REPLACE FUNCTION obter_qt_fat_ris (nr_seq_prescr_p bigint, nr_seq_diag_p bigint, nr_seq_fat_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w  bigint;


BEGIN

select  count(*)
into STRICT    qt_reg_w
from    pe_prescr_diag_fat_ris a,
        pe_prescr_diag b
where   b.nr_sequencia = a.nr_seq_diag
and     b.nr_seq_prescr = nr_seq_prescr_p
and     b.nr_seq_diag = nr_seq_diag_p
and     a.nr_seq_fat_ris = nr_seq_fat_p;

if (qt_reg_w > 0) then
  return 'S';
else
  return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
