SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_filme_prescr (nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_filme_w 		bigint := 0;
qt_total_w		bigint := 0;
nr_seq_procedimento_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	procedimento_paciente
	where	nr_prescricao = nr_prescricao_p
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	sum(coalesce(b.qt_filme,0))
	into STRICT	qt_filme_w
	from	proced_paciente_filme b
	where	b.nr_seq_proc = nr_seq_procedimento_w;

	if (qt_filme_w > 0) then
		qt_total_w := coalesce(qt_total_w,0) + qt_filme_w;
	end if;

	end;
end loop;
close C01;

return	qt_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_filme_prescr (nr_prescricao_p bigint) FROM PUBLIC;

