CREATE OR REPLACE FUNCTION obter_qt_fluxo ( qt_ar_p bigint, qt_oxigenio_p bigint) RETURNS bigint AS $body$
DECLARE


qt_fluxo_w	real;

BEGIN

select	coalesce(max(qt_fluxo),0)
into STRICT 	qt_fluxo_w
from	fluxo_oxigenio
where (qt_ar_p	>= qt_ar)
  and (qt_ar_p	< qt_ar+0.5)
  and (qt_oxigenio_p	>= qt_oxigenio)
  and (qt_oxigenio_p	<  qt_oxigenio+0.5);

return	qt_fluxo_w*100;

end	;
