CREATE OR REPLACE FUNCTION obter_qt_forn_cot_item ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint, ie_opcap_p text) RETURNS bigint AS $body$
DECLARE


qt_fornecedor_w		bigint;

/*ie_opcap_p
0=	Total de fornecedores
1=	Somente com preÃ§o informado
*/
BEGIN
if (ie_opcap_p = 0) then
	select	count(*)
	into STRICT	qt_fornecedor_w
	from	cot_compra_forn a,
		cot_compra_forn_item b
	where	a.nr_sequencia = b.nr_seq_cot_forn
	and	a.nr_cot_compra = nr_cot_compra_p
	and	b.nr_item_cot_compra = nr_item_cot_compra_p;
elsif (ie_opcap_p = 1) then
	select	count(*)
	into STRICT	qt_fornecedor_w
	from	cot_compra_forn a,
		cot_compra_forn_item b
	where	a.nr_sequencia = b.nr_seq_cot_forn
	and	a.nr_cot_compra = nr_cot_compra_p
	and	b.nr_item_cot_compra = nr_item_cot_compra_p
	and	coalesce(b.vl_unitario_material, 0) > 0;
end if;

return	qt_fornecedor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
