CREATE OR REPLACE FUNCTION obter_qt_frasco_pato (nr_seq_frasco_pato_p bigint) RETURNS bigint AS $body$
DECLARE


qt_frascos_w		bigint;
nr_seq_superior_w	bigint;

BEGIN

select	coalesce(max(nr_seq_frasco_sup),0)
into STRICT	nr_seq_superior_w
from	frasco_pato_loc
where	nr_sequencia= nr_seq_frasco_pato_p;

if (nr_seq_superior_w > 0) then
	select	count(*)
	into STRICT	qt_frascos_w
	from	frasco_pato_loc
	where	nr_seq_frasco_sup  = nr_seq_superior_w;
	qt_frascos_w := (qt_frascos_w+1);
else
	select	count(*)
	into STRICT	qt_frascos_w
	from	frasco_pato_loc
	where	nr_sequencia = nr_seq_frasco_pato_p;

end if;


return	qt_frascos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
