CREATE OR REPLACE FUNCTION obter_qt_horas_ativ_cron ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

nr_seq_etapa_cron_w	bigint;
qt_horas_acres_w	bigint;
ie_soma_subtrai_w	varchar(1);
qt_horas_cron_w		bigint;
qt_retorno_w		bigint;

BEGIN
select	max(nr_seq_etapa_cron),
		max(qt_horas_acres),
		max(ie_soma_subtrai)
into STRICT	nr_seq_etapa_cron_w,
		qt_horas_acres_w,
		ie_soma_subtrai_w
from	proj_alt_escopo_ativ
where	nr_sequencia = nr_sequencia_p;

select	qt_hora_prev
into STRICT	qt_horas_cron_w
from	proj_cron_etapa
where	nr_sequencia = nr_seq_etapa_cron_w;

qt_retorno_w := qt_horas_cron_w;

if (ie_soma_subtrai_w = 'P') then
	qt_retorno_w := qt_horas_cron_w + qt_horas_acres_w;
else	if (ie_soma_subtrai_w = 'N') then
			qt_retorno_w := qt_horas_cron_w - qt_horas_acres_w;
		end if;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
