CREATE OR REPLACE FUNCTION obter_qt_horas_consultoria ( dt_mes_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_registros_w		bigint;
qt_horas_w		double precision;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	proj_rat
where	trunc(dt_aprov_fin,'month') = trunc(dt_mes_referencia_p,'month');

if (qt_registros_w > 0) then

	select	sum(coalesce(qt_total_hora,0)) qt_horas
	into STRICT	qt_horas_w
	from	proj_rat
	where	trunc(dt_aprov_fin,'month') = trunc(dt_mes_referencia_p,'month');

else
	qt_horas_w := 0;
end if;

return	qt_horas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
