CREATE OR REPLACE FUNCTION obter_qt_hora_escala ( dt_inicio_p timestamp, dt_final_p timestamp, dt_inicio_p_p timestamp, dt_final_p_p timestamp, ie_feriado_p text) RETURNS bigint AS $body$
DECLARE


qt_hora_retorno_w	double precision := 0;
ie_dia_semana_inicio_w	smallint;
ie_dia_semana_final_w	smallint;
dt_aux_w		timestamp;


BEGIN

if (ie_feriado_p = 'S') then
	if (dt_inicio_p >= dt_inicio_p_p) and (dt_final_p <= dt_final_p_p) then
		qt_hora_retorno_w	:= dt_final_p - dt_inicio_p;
	elsif (dt_inicio_p >= dt_inicio_p_p) and (dt_final_p > dt_final_p_p) then
		dt_aux_w		:= trunc(dt_inicio_p + 1);
		qt_hora_retorno_w	:= dt_aux_w - dt_inicio_p;
	elsif (dt_inicio_p < dt_inicio_p_p) and (dt_final_p <= dt_final_p_p) then
		dt_aux_w		:= trunc(dt_final_p);
		qt_hora_retorno_w	:= dt_final_p - dt_aux_w;
	end if;
else
	begin
	select	pkg_date_utils.get_WeekDay(dt_inicio_p)
	into STRICT	ie_dia_semana_inicio_w
	;

	select	pkg_date_utils.get_WeekDay(dt_final_p)
	into STRICT	ie_dia_semana_final_w
	;

	if (ie_dia_semana_final_w in (1,7) and
		ie_dia_semana_inicio_w in (1,7)) then
		if (dt_inicio_p >= dt_inicio_p_p) and (dt_final_p <= dt_final_p_p) then
			qt_hora_retorno_w	:= dt_final_p - dt_inicio_p;
		elsif (dt_inicio_p >= dt_inicio_p_p) and (dt_final_p > dt_final_p_p) then
			dt_aux_w		:= trunc(dt_inicio_p + 1);
			qt_hora_retorno_w	:= dt_aux_w - dt_inicio_p;
		elsif (dt_inicio_p < dt_inicio_p_p) and (dt_final_p <= dt_final_p_p) then
			dt_aux_w		:= trunc(dt_final_p);
			qt_hora_retorno_w	:= dt_final_p - dt_aux_w;
		end if;
	elsif (ie_dia_semana_inicio_w in (1,7)) then
		if (dt_inicio_p >= dt_inicio_p_p) and (dt_final_p <= dt_final_p_p) then
			qt_hora_retorno_w	:= dt_final_p - dt_inicio_p;
		elsif (dt_inicio_p >= dt_inicio_p_p) and (dt_final_p > dt_final_p_p) then
			dt_aux_w		:= trunc(dt_inicio_p + 1);
			qt_hora_retorno_w	:= dt_aux_w - dt_inicio_p;
		elsif (dt_inicio_p < dt_inicio_p_p) and (dt_final_p <= dt_final_p_p) then
			dt_aux_w		:= trunc(dt_final_p);
			qt_hora_retorno_w	:= dt_final_p - dt_aux_w;
		end if;
	elsif (ie_dia_semana_final_w in (1,7)) then
		if (dt_inicio_p >= dt_inicio_p_p) and (dt_final_p <= dt_final_p_p) then
			qt_hora_retorno_w	:= dt_final_p - dt_inicio_p;
		elsif (dt_inicio_p >= dt_inicio_p_p) and (dt_final_p > dt_final_p_p) then
			dt_aux_w		:= trunc(dt_inicio_p + 1);
			qt_hora_retorno_w	:= dt_aux_w - dt_inicio_p;
		elsif (dt_inicio_p < dt_inicio_p_p) and (dt_final_p <= dt_final_p_p) then
			dt_aux_w		:= trunc(dt_final_p);
			qt_hora_retorno_w	:= dt_final_p - dt_aux_w;
		end if;
	end if;

	end;
end if;

qt_hora_retorno_w	:= qt_hora_retorno_w * 24;

return qt_hora_retorno_w;

END;
$body$
