CREATE OR REPLACE FUNCTION obter_qt_hrs_curta_duracao ( cd_perfil_p bigint, cd_medico_p text, cd_especialidade_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_horas_w 			regra_plt_curta_duracao.qt_horas_validade%type	:= 0;
cd_especialidade_w	especialidade_medica.cd_especialidade%type;
cd_perfil_w			perfil.cd_perfil%type;
ie_retorno_w		char(1);
				

BEGIN 
 
select 	coalesce(max('S'),'N') 
into STRICT	ie_retorno_w 
from 	regra_plt_curta_duracao LIMIT 1;
	 
if (ie_retorno_w = 'S') then	 
	cd_perfil_w			:= coalesce(cd_perfil_p, 0);
	cd_especialidade_w	:= coalesce(cd_especialidade_p, coalesce(obter_especialidade_medico2(cd_medico_p),0));
	 
	select 	coalesce(max(qt_horas_validade),0) 
	into STRICT	qt_horas_w 
	from 	regra_plt_curta_duracao 
	where 	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w 
	and		coalesce(cd_especialidade,cd_especialidade_w) = cd_especialidade_w;	
end if;
 
return qt_horas_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
