CREATE OR REPLACE FUNCTION obter_qt_infec_atend (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(60);
nr_atendimento_w	bigint;
qt_tmp_w		bigint;
qt_infec_w		bigint;

c01 CURSOR FOR 
	SELECT	nr_atendimento 
	from	atendimento_paciente 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w 
	order 	by nr_atendimento;
			

BEGIN 
 
if (nr_atendimento_p > 0) then 
 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	atendimento_paciente 
	where	nr_atendimento 	= nr_atendimento_p;
 
	qt_infec_w := 0;
	 
	open c01;
	loop 
	fetch c01 into	 
		nr_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		 
		select	count(*) 
		into STRICT	qt_tmp_w 
		from	cih_ficha_ocorrencia f, 
			cih_local_infeccao i 
		where	f.nr_ficha_ocorrencia	= i.nr_ficha_ocorrencia 
		and	f.nr_atendimento	= nr_atendimento_w;
		 
		qt_infec_w := qt_infec_w + qt_tmp_w;
		 
		end;
	end loop;
	close c01;
 
end if;
 
 
return	qt_infec_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_infec_atend (nr_atendimento_p bigint) FROM PUBLIC;

