SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_infec_niss (dt_inicial_p timestamp, dt_final_p timestamp, ie_opc_p text, cd_setor_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

ie_infeccao_w bigint;

/*
Opção
TG - Total de episodios IH Geral
 TP - Total de pacientes com ih Geral.
 PP - Percentual de Paciente IH geral.
 TGS  - Total de episodios IH por Setor
TPS- Total de pacientes com ih
 PS - Percentual de Paciente IH geral.
 */
BEGIN
							--  Total de episodios IH Geral --
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (ie_opc_p = 'TG') then
	select	count(*)
	into STRICT	ie_infeccao_w
	from	cih_Caso_infeccao f,
		setor_atendimento c,
		cih_local_infeccao a,
		cih_ficha_ocorrencia b
	where	a.dt_infeccao between dt_inicial_p and dt_final_p
	and     a.cd_setor_atendimento = c.cd_setor_atendimento
	and	a.cd_caso_infeccao = f.cd_caso_infeccao
	and	a.nr_ficha_ocorrencia	=	b.nr_ficha_ocorrencia
	and	coalesce((f.ie_tipo_Caso),f.cd_caso_infeccao) <> ('2')
	and     c.cd_classif_setor in (3,4)
    and 	c.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;
end if;

							--  Total de pacientes com ih Geral. --
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (ie_opc_p = 'TP') then

	select count(*)
	into STRICT 	ie_infeccao_w
	from(
	SELECT	distinct b.nr_atendimento
	from	cih_Caso_infeccao f,
		setor_atendimento c,
		cih_local_infeccao a,
		cih_ficha_ocorrencia b
	where	a.dt_infeccao between dt_inicial_p and dt_final_p
	and	a.cd_caso_infeccao = f.cd_caso_infeccao
	and     a.cd_setor_atendimento = c.cd_setor_atendimento
	and	a.nr_ficha_ocorrencia	=	b.nr_ficha_ocorrencia
	and	coalesce((f.ie_tipo_Caso),f.cd_caso_infeccao) <> ('2')
	and     c.cd_classif_setor in (3,4)
	and 	c.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento);
end if;
							-- PP - Total de Paciente Internado. --
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (ie_opc_p = 'PP') then

	select  sum(CASE WHEN A.IE_periodo='D' THEN  NR_PACIENTES  ELSE 0 END )
	into STRICT    ie_infeccao_w
	from    setor_atendimento b,
		EIS_OCUPACAO_HOSPITALAR a
	where   a.cd_setor_atendimento = b.cd_setor_atendimento
	and 	b.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
	and     a.dt_referencia between dt_inicial_p and dt_final_p
	and     b.cd_classif_setor in (3,4);
end if;

							--  Total de episodios IH setor --
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (ie_opc_p = 'TGS') then
	select	count(*)
	into STRICT	ie_infeccao_w
	from	cih_Caso_infeccao f,
		setor_atendimento c,
		cih_local_infeccao a,
		cih_ficha_ocorrencia b
	where	a.dt_infeccao between dt_inicial_p and dt_final_p
	and     a.cd_setor_atendimento = c.cd_setor_atendimento
	and	a.cd_caso_infeccao = f.cd_caso_infeccao
	and     c.cd_setor_atendimento = cd_setor_atendimento_p
	and	a.nr_ficha_ocorrencia	=	b.nr_ficha_ocorrencia
	and	coalesce((f.ie_tipo_Caso),f.cd_caso_infeccao) <> ('2')
	and     c.cd_classif_setor in (3,4)
	and 	c.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;
end if;

							--  Total de pacientes com ih por Setor --
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (ie_opc_p = 'TPS') then

	select count(*)
	into STRICT 	ie_infeccao_w
	from(
	SELECT	distinct b.nr_atendimento
	from	cih_Caso_infeccao f,
		setor_atendimento c,
		cih_local_infeccao a,
		cih_ficha_ocorrencia b
	where	a.dt_infeccao between dt_inicial_p and dt_final_p
	and	a.cd_caso_infeccao = f.cd_caso_infeccao
	and	a.nr_ficha_ocorrencia	=	b.nr_ficha_ocorrencia
	and	coalesce((f.ie_tipo_Caso),f.cd_caso_infeccao) <> ('2')
	and     c.cd_classif_setor in (3,4)
    and     a.cd_setor_atendimento = cd_setor_atendimento_p
	and 	c.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento);
end if;

							-- Total de Paciente Internado.por setor--
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (ie_opc_p = 'PS') then

	select  sum(CASE WHEN A.IE_periodo='D' THEN  NR_PACIENTES  ELSE 0 END )
	into STRICT    ie_infeccao_w
	from    setor_atendimento b,
		EIS_OCUPACAO_HOSPITALAR a
	where   a.cd_setor_atendimento = b.cd_setor_atendimento
	and 	b.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
	and     dt_referencia between dt_inicial_p and dt_final_p
	and     b.cd_classif_setor in (3,4)
	and     a.cd_setor_atendimento = cd_setor_atendimento_p;
end if;

return ie_infeccao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_infec_niss (dt_inicial_p timestamp, dt_final_p timestamp, ie_opc_p text, cd_setor_atendimento_p bigint) FROM PUBLIC;

