SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_inicio_pend_oci ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w				double precision;
cd_unidade_medida_compra_w		varchar(30);
qt_conversao_w				double precision;
cd_estabelecimento_w			smallint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

select	substr(obter_dados_material_estab(a.cd_material,cd_estabelecimento_w,'UMC'),1,255) cd_unidade_medida_compra,
	a.qt_conv_compra_estoque
into STRICT	cd_unidade_medida_compra_w,
	qt_conversao_w
from	ordem_compra_item b,
	material a
where	a.cd_material = b.cd_material
and	b.nr_item_oci = nr_item_oci_p
and	b.nr_ordem_compra = nr_ordem_compra_p;

select	coalesce(sum(
		CASE WHEN b.cd_unidade_medida_compra=cd_unidade_medida_compra_w THEN  coalesce(a.qt_prevista_entrega,0) * qt_conversao_w  ELSE coalesce(a.qt_prevista_entrega,0) END  -
		CASE WHEN b.cd_unidade_medida_compra=cd_unidade_medida_compra_w THEN  coalesce(a.qt_real_entrega,0) * qt_conversao_w  ELSE coalesce(a.qt_real_entrega,0) END ),0)
into STRICT	qt_retorno_w
from	ordem_compra_item b,
	ordem_compra_item_entrega a
where	a.nr_ordem_compra = nr_ordem_compra_p
and	a.nr_item_oci = nr_item_oci_p
and	a.nr_ordem_compra = b.nr_ordem_compra
and	a.nr_item_oci = b.nr_item_oci
and	(dt_inicio_pendencia IS NOT NULL AND dt_inicio_pendencia::text <> '')
and	coalesce(dt_fim_pendencia::text, '') = ''
and	coalesce(a.dt_cancelamento::text, '') = '';

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
