SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_inspecao ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, dt_prevista_entrega_p timestamp) RETURNS bigint AS $body$
DECLARE

 
qt_inspecionados_w		double precision;
qt_entregue_w		double precision;
qt_retorno_w		double precision;
ie_permite_w		varchar(10) := 'S';
nr_seq_contagem_w	bigint;
qt_devolvidos_w		double precision;

 

BEGIN 
 
qt_devolvidos_w := 0;
 
select	coalesce(max(b.nr_seq_contagem),0) 
into STRICT	nr_seq_contagem_w 
from	inspecao_recebimento a, 
	inspecao_contagem b 
where	a.nr_sequencia = b.nr_seq_inspecao 
and	a.nr_ordem_compra = nr_ordem_compra_p 
and	a.nr_item_oci = nr_item_oci_p 
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') 
and	((coalesce(a.nr_seq_tipo_nao_conf::text, '') = '') or 
	((a.nr_seq_tipo_nao_conf IS NOT NULL AND a.nr_seq_tipo_nao_conf::text <> '') and (obter_se_permite_nova_oc(a.nr_seq_tipo_nao_conf) = 'N'))) 
and	((coalesce(dt_prevista_entrega_p::text, '') = '') or 
	((dt_prevista_entrega_p IS NOT NULL AND dt_prevista_entrega_p::text <> '') and (trunc(obter_dt_prev_oci_inspecao(a.nr_sequencia)) = trunc(dt_prevista_entrega_p)))) 
and not exists ( 
	SELECT	1 
	from	nota_fiscal x, 
		nota_fiscal_item y, 
		operacao_nota o 
	where	x.cd_operacao_nf = o.cd_operacao_nf 
	and	x.nr_Sequencia = y.nr_sequencia 
	and	y.nr_ordem_compra = a.nr_ordem_compra 
	and	y.nr_item_oci = a.nr_item_oci 
	and	o.ie_devolucao = 'S' 
	and	x.nr_sequencia_ref = a.nr_seq_nota_fiscal);
	 
if (nr_seq_contagem_w > 0) then 
 
	select	coalesce(sum(b.qt_inspecao),0) 
	into STRICT	qt_inspecionados_w 
	FROM inspecao_contagem b, inspecao_recebimento a
LEFT OUTER JOIN inspecao_registro c ON (a.nr_seq_registro = c.nr_sequencia)
WHERE a.nr_sequencia = b.nr_seq_inspecao  and a.nr_ordem_compra = nr_ordem_compra_p and a.nr_item_oci = nr_item_oci_p and (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') and coalesce(c.dt_devolucao::text, '') = '' and b.nr_seq_contagem = nr_seq_contagem_w and ((coalesce(a.nr_seq_tipo_nao_conf::text, '') = '') or 
		((a.nr_seq_tipo_nao_conf IS NOT NULL AND a.nr_seq_tipo_nao_conf::text <> '') and (obter_se_permite_nova_oc(a.nr_seq_tipo_nao_conf) = 'N'))) and ((coalesce(dt_prevista_entrega_p::text, '') = '') or 
		((dt_prevista_entrega_p IS NOT NULL AND dt_prevista_entrega_p::text <> '') and (trunc(obter_dt_prev_oci_inspecao(a.nr_sequencia)) = trunc(dt_prevista_entrega_p)))) and not exists ( 
		SELECT	1 
		from	nota_fiscal x, 
			nota_fiscal_item y, 
			operacao_nota o 
		where	x.cd_operacao_nf = o.cd_operacao_nf 
		and	x.nr_Sequencia = y.nr_sequencia 
		and	y.nr_ordem_compra = a.nr_ordem_compra 
		and	y.nr_item_oci = a.nr_item_oci 
		and	o.ie_devolucao = 'S' 
		and	x.nr_sequencia_ref = a.nr_seq_nota_fiscal);
else	 
 
	 
	select	coalesce(sum(a.qt_inspecao),0) 
	into STRICT	qt_inspecionados_w 
	FROM inspecao_recebimento a
LEFT OUTER JOIN inspecao_registro b ON (a.nr_seq_registro = b.nr_sequencia)
WHERE a.nr_ordem_compra = nr_ordem_compra_p  and a.nr_item_oci = nr_item_oci_p and coalesce(b.dt_devolucao::text, '') = '' and ((coalesce(a.nr_seq_tipo_nao_conf::text, '') = '') or 
		((a.nr_seq_tipo_nao_conf IS NOT NULL AND a.nr_seq_tipo_nao_conf::text <> '') and (obter_se_permite_nova_oc(a.nr_seq_tipo_nao_conf) = 'N'))) /*and not exists( 
		select	1 
		from	nota_fiscal x, 
			nota_fiscal_item y, 
			operacao_nota o 
		where	x.cd_operacao_nf = o.cd_operacao_nf 
		and	x.nr_Sequencia = y.nr_sequencia 
		and	y.nr_ordem_compra = a.nr_ordem_compra 
		and	y.nr_item_oci = a.nr_item_oci 
		and	o.ie_devolucao = 'S' 
		and	x.nr_sequencia_ref = a.nr_seq_nota_fiscal)*/
  and ((coalesce(dt_prevista_entrega_p::text, '') = '') or 
		((dt_prevista_entrega_p IS NOT NULL AND dt_prevista_entrega_p::text <> '') and (trunc(obter_dt_prev_oci_inspecao(a.nr_sequencia)) = trunc(dt_prevista_entrega_p))));
		 
	 
	/*OS 958752	 
	Exemplo 01: 
		Tem uma inspeção de 50 itens 
		Gera uma nota de 50 itens, e calcula. 
		Depois devolve 40 itens. 
		Esse cálculo serve para dizer que teve 40 devoluções 
		 
		qt_inspecionados_w = 50 
		qt_devolvidos_w = 40 
		Ou seja, tem 10 entregues 
	*/
 
	 
	select	coalesce(sum(y.qt_item_nf),0) 
	into STRICT	qt_devolvidos_w 
	from	nota_fiscal x, 
		nota_fiscal_item y, 
		operacao_nota o, 
		inspecao_recebimento a 
	where	x.cd_operacao_nf = o.cd_operacao_nf 
	and	x.nr_Sequencia = y.nr_sequencia 
	and	y.nr_ordem_compra = a.nr_ordem_compra 
	and	y.nr_item_oci = a.nr_item_oci 
	and	a.nr_ordem_compra = nr_ordem_compra_p 
	and	a.nr_item_oci = nr_item_oci_p 
	and	o.ie_devolucao = 'S' 
	and	x.nr_sequencia_ref = a.nr_seq_nota_fiscal 
	and	((coalesce(dt_prevista_entrega_p::text, '') = '') or 
		((dt_prevista_entrega_p IS NOT NULL AND dt_prevista_entrega_p::text <> '') and (trunc(obter_dt_prev_oci_inspecao(a.nr_sequencia)) = trunc(dt_prevista_entrega_p))));
 
	qt_inspecionados_w := qt_inspecionados_w - qt_devolvidos_w;
		 
end if;
 
/*São as entregas que não passaram por inspeção*/
	 
select	coalesce(sum(a.qt_real_entrega),0) 
into STRICT	qt_entregue_w 
from	ordem_compra_item_entrega a 
where	a.nr_ordem_compra = nr_ordem_compra_p 
and	a.nr_item_oci = nr_item_oci_p 
and	((coalesce(dt_prevista_entrega_p::text, '') = '') or 
	((dt_prevista_entrega_p IS NOT NULL AND dt_prevista_entrega_p::text <> '') and (trunc(a.dt_prevista_entrega) = trunc(dt_prevista_entrega_p)))) 
and	not exists (SELECT	1 
		  from	inspecao_recebimento x 
		  where	x.nr_seq_entrega = a.nr_sequencia 
		  and	x.nr_ordem_compra = a.nr_ordem_compra 
		  and	x.nr_item_oci = a.nr_item_oci);
 
 
	   
qt_retorno_w := qt_inspecionados_w + qt_entregue_w;
 
return	qt_retorno_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_inspecao ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, dt_prevista_entrega_p timestamp) FROM PUBLIC;

