CREATE OR REPLACE FUNCTION obter_qt_inv_ciclo ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, nr_seq_ciclo_p bigint, dt_inicio_consulta_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_inventario_w		bigint;
ds_retorno_w		varchar(1);


BEGIN

begin
select	count(*)
into STRICT	qt_inventario_w
from	inventario a
where	a.cd_estabelecimento = cd_estabelecimento_p
and	a.cd_local_estoque = cd_local_estoque_p
and	(a.dt_atualizacao_saldo IS NOT NULL AND a.dt_atualizacao_saldo::text <> '')
and	trunc(a.dt_bloqueio) >= trunc(dt_inicio_consulta_p)
and	nr_seq_ciclo = nr_seq_ciclo_p;
exception
when others then
	qt_inventario_w	:= 0;
end;

return	qt_inventario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
