CREATE OR REPLACE FUNCTION obter_qt_irrigacao_adep_atx (nr_seq_irrigacao_p bigint, qt_infusao_p bigint,qt_drenagem_p bigint) RETURNS bigint AS $body$
DECLARE


qt_volume_infundido_w	bigint;
qt_volume_drenado_w		bigint;
qt_volume_real_w		bigint;
BEGIN

select 	sum(coalesce(qt_infusao,0)) + qt_infusao_p ,
		sum(coalesce(qt_drenagem,0)) + qt_drenagem_p
into STRICT	qt_volume_infundido_w,
		qt_volume_drenado_w
from	adep_irrigacao_etapa a,
		adep_irrigacao b
where	a.nr_seq_irrigacao = b.nr_sequencia
and		b.nr_sequencia = nr_seq_irrigacao_p
and		coalesce(a.ie_evento_valido,'S')	= 'S';

qt_volume_real_w := qt_volume_infundido_w - qt_volume_drenado_w;

if (qt_volume_real_w < 0) then
	qt_volume_real_w	:= qt_volume_real_w * -1;
end if;

return	qt_volume_real_w;

end;
$body$
LANGUAGE PLPGSQL
