CREATE OR REPLACE FUNCTION obter_qt_item_cotacao ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint) RETURNS bigint AS $body$
DECLARE



qt_material_w		double precision;

/*Retorna a quantidade original do item na cotação.
Utilizo para verificar se algum item foi alterado a quantidade em determinado fornecedor*/
BEGIN

select	coalesce(max(qt_material),0)
into STRICT	qt_material_w
from	cot_compra_item
where	nr_cot_compra		= nr_cot_compra_p
and	nr_item_cot_compra	= nr_item_cot_compra_p;

RETURN	qt_material_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
