CREATE OR REPLACE FUNCTION obter_qt_item_max_receita (cd_lista_p text, cd_medicamento_p text) RETURNS bigint AS $body$
DECLARE


qt_item_max_w	 bigint;


BEGIN

select      coalesce(max(qt_item_max),999)
into STRICT	qt_item_max_w
from	regra_geracao_receita
where	cd_lista 	             = cd_lista_p
and	cd_medicamento  = cd_medicamento_p;

return qt_item_max_w;

end;
$body$
