CREATE OR REPLACE FUNCTION obter_qt_item_ordem_nota ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS bigint AS $body$
DECLARE


qt_item_nf_w			double precision;


BEGIN

select	coalesce(sum(b.qt_item_nf),0)
into STRICT	qt_item_nf_w
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	a.ie_situacao	= '1'
and	(a.dt_atualizacao_estoque IS NOT NULL AND a.dt_atualizacao_estoque::text <> '')
and	b.nr_ordem_compra	= nr_ordem_compra_p
and	b.nr_item_oci	= nr_item_oci_p;

return	qt_item_nf_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
