SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_item_receber_transf (nr_seq_nota_p bigint, nr_ordem_compra_p bigint, nr_item_oci_p bigint, nr_seq_lote_fornec_p bigint, cd_unidade_medida_retorno_p text default 'UME') RETURNS bigint AS $body$
DECLARE


/*Opcoes cd_unidade_medida_retorno_p:
UME = Unidade Medida Estoque
UMC = Unidade Medida Consumo
UMP = Unidade Medida Compra*/
ds_retorno_w		double precision;
nr_nota_fiscal_w	nota_fiscal.nr_nota_fiscal%type;
cd_material_w		nota_fiscal_item.cd_material%type;
cd_un_medida_compra_w	nota_fiscal_item.cd_unidade_medida_compra%type;
qt_item_nf_w		double precision;
qt_item_nf_entrada_w	double precision;


BEGIN

select	a.nr_nota_fiscal,
	b.cd_material,
	b.cd_unidade_medida_compra,
	sum(b.qt_item_nf)
into STRICT	nr_nota_fiscal_w,
	cd_material_w,
	cd_un_medida_compra_w,
	qt_item_nf_w
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	a.nr_sequencia = nr_seq_nota_p
and	b.nr_ordem_compra = nr_ordem_compra_p
and	b.nr_item_oci = nr_item_oci_p
and	(((nr_seq_lote_fornec_p IS NOT NULL AND nr_seq_lote_fornec_p::text <> '') and b.nr_seq_lote_fornec = nr_seq_lote_fornec_p) or (coalesce(nr_seq_lote_fornec_p::text, '') = '' and coalesce(b.nr_seq_lote_fornec::text, '') = ''))
group by a.nr_nota_fiscal,
	b.cd_material,
	b.cd_unidade_medida_compra;

select	coalesce(sum(qt_item_nf),0)
into STRICT	qt_item_nf_entrada_w
from	nota_fiscal x,
	nota_fiscal_item y
where	x.nr_sequencia = y.nr_sequencia
and	x.nr_nota_fiscal = nr_nota_fiscal_w
and	x.cd_serie_nf = y.cd_serie_nf
and	x.ie_situacao = '1'
and	y.nr_ordem_compra = nr_ordem_compra_p
and	y.nr_item_oci = nr_item_oci_p
and	obter_se_nota_entrada_saida(x.nr_sequencia) = 'E'
and	(((nr_seq_lote_fornec_p IS NOT NULL AND nr_seq_lote_fornec_p::text <> '') and y.nr_seq_lote_fornec = nr_seq_lote_fornec_p)
    or ((y.nr_seq_lote_fornec IS NOT NULL AND y.nr_seq_lote_fornec::text <> '')
        and not exists (SELECT	1
                        from	nota_fiscal a,
                            nota_fiscal_item b
                        where	a.nr_sequencia = b.nr_sequencia
                        and	b.nr_ordem_compra = nr_ordem_compra_p
                        and	obter_se_nota_entrada_saida(a.nr_sequencia) = 'S'
                        and	b.nr_item_oci = nr_item_oci_p
                        and	b.nr_seq_lote_fornec = y.nr_seq_lote_fornec)) 
    or (coalesce(nr_seq_lote_fornec_p::text, '') = '' and coalesce(y.nr_seq_lote_fornec::text, '') = ''));

ds_retorno_w := obter_quantidade_convertida(cd_material_w,
					qt_item_nf_w - qt_item_nf_entrada_w,
					cd_un_medida_compra_w,
					cd_unidade_medida_retorno_p);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
