CREATE OR REPLACE FUNCTION obter_qt_item_selecionado_conf (nr_ordem_compra_w bigint) RETURNS bigint AS $body$
DECLARE


qt_selecionados_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_selecionados_w
from	ordem_compra_item_conf
where	nr_ordem_compra = nr_ordem_compra_w
and	coalesce(dt_liberacao::text, '') = ''
and	coalesce(dt_estorno::text, '') = ''
and	(dt_selecao IS NOT NULL AND dt_selecao::text <> '');

return	qt_selecionados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
