CREATE OR REPLACE FUNCTION obter_qt_janelas_panorama (cd_perfil_p regra_perm_panorama.cd_perfil%type) RETURNS bigint AS $body$
DECLARE



qt_janelas_w	regra_perm_panorama.qt_janelas_simultaneas%type;


BEGIN

select	max(qt_janelas_simultaneas)
into STRICT 	qt_janelas_w
from	regra_perm_panorama
where	cd_perfil = cd_perfil_p;

if (coalesce(qt_janelas_w::text, '') = '') then  -- se n√£o encontrou a regra para o perfil, busca uma regra sem perfil definido.
	select	max(qt_janelas_simultaneas)
	into STRICT 	qt_janelas_w
	from	regra_perm_panorama
	where	coalesce(cd_perfil::text, '') = '';
end if;

return	coalesce(qt_janelas_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
