CREATE OR REPLACE FUNCTION obter_qt_linha_apac ( nr_seq_apac_p bigint) RETURNS bigint AS $body$
DECLARE


qt_registro_w	bigint;
qt_retorno_w	bigint := 1;


BEGIN

/*select	count(*)
into	qt_registro_w
from	procedimento_paciente a,
	w_susapac_interf b
where	a.nr_interno_conta	= b.nr_interno_conta
and	b.nr_sequencia	= nr_seq_apac_p
and	a.cd_motivo_exc_conta is null; Comentado para a OS 297906.
Pois estava retornando a quantidade errada de procedimento gerando APAC's duplicadas. Geliard 04/03/2011*/
select	count(distinct cd_procedimento)
into STRICT	qt_registro_w
from	procedimento_paciente a,
	w_susapac_interf b
where	a.nr_interno_conta = b.nr_interno_conta
and	b.nr_sequencia  = nr_seq_apac_p
and	a.ie_origem_proced = 7
and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

if (qt_registro_w < 10) then
	qt_retorno_w	:= 1;
else
	begin
	if (qt_registro_w > 10) then
		qt_retorno_w	:= 2;
	end if;
	if (qt_registro_w > 20) then
		qt_retorno_w	:= 3;
	end if;
	if (qt_registro_w > 30) then
		qt_retorno_w	:= 4;
	end if;
	if (qt_registro_w > 40) then
		qt_retorno_w	:= 5;
	end if;
	if (qt_registro_w > 50) then
		qt_retorno_w	:= 6;
	end if;
	if (qt_registro_w > 60) then
		qt_retorno_w	:= 7;
	end if;
	if (qt_registro_w > 70) then
		qt_retorno_w	:= 8;
	end if;
	if (qt_registro_w > 80) then
		qt_retorno_w	:= 9;
	end if;
	if (qt_registro_w > 90) then
		qt_retorno_w	:= 10;
	end if;
	if (qt_registro_w > 100) then
		qt_retorno_w	:= 11;
	end if;
	end;
end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
