SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_livres_fixos ( cd_setor_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

qt_retorno_w		bigint := 0;
qt_setores_total_w	bigint;
qt_setores_ocupa_w	bigint;

BEGIN

if (coalesce(cd_setor_atendimento_p,0) > 0) then

	select	count(*)
	into STRICT	qt_setores_total_w
	from	unidade_atendimento
	where	cd_setor_atendimento 	= cd_setor_atendimento_p
	and	coalesce(ie_temporario,'N') 	= 'N'
	and	ie_situacao 		= 'A';

	select	count(*)
	into STRICT	qt_setores_ocupa_w
	from	unidade_atendimento
	where	cd_setor_atendimento 	= cd_setor_atendimento_p
	and	coalesce(ie_temporario,'N') 	= 'N'
	and	ie_status_unidade	= 'P'
	and	ie_situacao 		= 'A';

qt_retorno_w	:= (qt_setores_total_w	- qt_setores_ocupa_w);
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
