SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_lote_fornec_utilizado ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE



qt_material_w	double precision;
qt_requisicao_w	double precision;


BEGIN

select	coalesce(sum(qt_material), 0)
into STRICT	qt_material_w
from	material_atend_paciente
where	nr_seq_lote_fornec = nr_sequencia_p;

select 	coalesce(sum(CASE WHEN c.ie_entrada_saida='S' THEN b.qt_material_atendida WHEN c.ie_entrada_saida='E' THEN b.qt_material_atendida * -1 END ),0)
into STRICT	qt_requisicao_w
from 	requisicao_material a,
	item_requisicao_material b,
	operacao_estoque c,
	material d
where	a.nr_requisicao = b.nr_requisicao
and	b.cd_material = d.cd_material
and	a.cd_operacao_estoque 	= c.cd_operacao_estoque
and	b.nr_seq_lote_fornec	= nr_sequencia_p
and	c.ie_consumo <> 'N'
and	obter_tipo_motivo_baixa_req(b.cd_motivo_baixa) in (1,4);

qt_material_w := qt_material_w + qt_requisicao_w;

return qt_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
